import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import api from '../../services/api';
import './PublicSearch.css';

function PublicSearch() {
  const [lawyers, setLawyers] = useState([]);
  const [loading, setLoading] = useState(false);
  const [filters, setFilters] = useState({
    search: '',
    region: '',
    area_of_law: '',
    service: '',
    license_status: ''
  });
  const [options, setOptions] = useState({
    regions: [],
    areasOfLaw: [],
    services: []
  });

  useEffect(() => {
    fetchOptions();
    fetchLawyers();
  }, []);

  useEffect(() => {
    fetchLawyers();
  }, [filters]);

  const fetchOptions = async () => {
    try {
      // Fetch all lawyers to extract unique filter options
      const response = await api.get('/lawyers');
      console.log('fetchOptions response:', response);
      const allLawyers = Array.isArray(response.data) ? response.data : (response.data.data || []);
      console.log('Extracted lawyers for options:', allLawyers);
      
      if (!Array.isArray(allLawyers) || allLawyers.length === 0) {
        console.warn('No lawyers data returned');
        return;
      }
      
      // Extract unique regions from locations
      const uniqueRegions = [...new Set(
        allLawyers
          .flatMap(l => (l.locations && Array.isArray(l.locations)) ? l.locations.map(loc => loc.region) : [])
          .filter(Boolean)
      )].sort();

      // Extract unique areas of law
      const uniqueAreas = [...new Set(
        allLawyers
          .flatMap(l => (l.areas_of_law && Array.isArray(l.areas_of_law)) ? l.areas_of_law.map(a => a.area_name) : [])
          .filter(Boolean)
      )].sort();

      // Extract unique services
      const uniqueServices = [...new Set(
        allLawyers
          .flatMap(l => (l.services && Array.isArray(l.services)) ? l.services : [])
          .filter(Boolean)
      )].sort();

      console.log('Extracted options - regions:', uniqueRegions, 'areas:', uniqueAreas, 'services:', uniqueServices);

      setOptions({
        regions: uniqueRegions,
        areasOfLaw: uniqueAreas,
        services: uniqueServices
      });
    } catch (error) {
      console.error('Error fetching options:', error.message, error);
    }
  };

  const fetchLawyers = async () => {
    setLoading(true);
    try {
      // Fetch all lawyers from the API
      const response = await api.get('/lawyers');
      console.log('fetchLawyers response:', response);
      let allLawyers = Array.isArray(response.data) ? response.data : (response.data.data || []);
      console.log('Total lawyers fetched:', allLawyers.length);
      console.log('First lawyer sample:', allLawyers[0]);

      // Apply client-side filtering
      const filtered = allLawyers.filter(lawyer => {
        // Search filter
        if (filters.search) {
          const q = filters.search.toLowerCase();
          const nameMatch = (lawyer.name || '').toLowerCase().includes(q);
          const emailMatch = (lawyer.email || '').toLowerCase().includes(q);
          if (!nameMatch && !emailMatch) return false;
        }

        // Region filter
        if (filters.region) {
          const hasRegion = lawyer.locations && Array.isArray(lawyer.locations) && 
                           lawyer.locations.some(loc => loc.region === filters.region);
          if (!hasRegion) return false;
        }

        // Area of law filter
        if (filters.area_of_law) {
          const hasArea = lawyer.areas_of_law && Array.isArray(lawyer.areas_of_law) && 
                         lawyer.areas_of_law.some(a => a.area_name === filters.area_of_law);
          if (!hasArea) return false;
        }

        // Service filter
        if (filters.service) {
          const hasService = lawyer.services && Array.isArray(lawyer.services) && 
                            lawyer.services.includes(filters.service);
          if (!hasService) return false;
        }

        // License status filter
        if (filters.license_status) {
          if (lawyer.license_status !== filters.license_status) return false;
        }

        return true;
      });

      console.log('Filtered lawyers:', filtered.length, 'with filters:', filters);
      setLawyers(filtered);
    } catch (error) {
      console.error('Error fetching lawyers:', error.message, error);
      setLawyers([]);
    } finally {
      setLoading(false);
    }
  };

  const handleFilterChange = (key, value) => {
    setFilters(prev => ({ ...prev, [key]: value }));
  };

  return (
    <div className="container">
      <div className="search-header">
        <h1>Find Legal Aid Providers</h1>
        <p>Search for lawyers, law firms, and legal aid organizations</p>
      </div>

      <div className="card">
        <div className="filters">
          <div className="form-group">
            <label>Search by Name or Email</label>
            <input
              type="text"
              value={filters.search}
              onChange={(e) => handleFilterChange('search', e.target.value)}
              placeholder="Enter name or email..."
            />
          </div>

          <div className="form-group">
            <label>Region</label>
            <select
              value={filters.region}
              onChange={(e) => handleFilterChange('region', e.target.value)}
            >
              <option value="">All Regions</option>
              {options.regions.map(region => (
                <option key={region} value={region}>{region}</option>
              ))}
            </select>
          </div>

          <div className="form-group">
            <label>Area of Law</label>
            <select
              value={filters.area_of_law}
              onChange={(e) => handleFilterChange('area_of_law', e.target.value)}
            >
              <option value="">All Areas</option>
              {options.areasOfLaw.map(area => (
                <option key={area} value={area}>{area}</option>
              ))}
            </select>
          </div>

          <div className="form-group">
            <label>Service Type</label>
            <select
              value={filters.service}
              onChange={(e) => handleFilterChange('service', e.target.value)}
            >
              <option value="">All Services</option>
              {options.services.map(service => (
                <option key={service} value={service}>{service}</option>
              ))}
            </select>
          </div>

          <div className="form-group">
            <label>License Status</label>
            <select
              value={filters.license_status}
              onChange={(e) => handleFilterChange('license_status', e.target.value)}
            >
              <option value="">All</option>
              <option value="ACTIVE">Active</option>
              <option value="INACTIVE">Inactive</option>
            </select>
          </div>
        </div>
      </div>

      {loading ? (
        <div className="loading">Loading...</div>
      ) : (
        <div className="lawyers-grid">
          {lawyers.map(lawyer => (
            <div key={lawyer.lawyer_id} className="lawyer-card">
              <h3>{lawyer.name}</h3>
              <p className="lawyer-type">{lawyer.provider_type}</p>
              
              <div className="lawyer-info">
                {lawyer.license_status && (
                  <span className={`badge ${lawyer.license_status === 'ACTIVE' ? 'badge-success' : 'badge-danger'}`}>
                    {lawyer.license_status}
                  </span>
                )}
                {lawyer.verified && (
                  <span className="badge badge-info">Verified</span>
                )}
              </div>

              {lawyer.locations && lawyer.locations.length > 0 && (
                <p className="lawyer-location">
                  üìç {lawyer.locations[0].region}
                  {lawyer.locations[0].district && `, ${lawyer.locations[0].district}`}
                </p>
              )}

              {lawyer.phone && (
                <p className="lawyer-contact">üìû {lawyer.phone}</p>
              )}

              {lawyer.email && (
                <p className="lawyer-contact">‚úâÔ∏è {lawyer.email}</p>
              )}

              <Link to={`/lawyer/${lawyer.lawyer_id}`} className="btn btn-primary">
                View Details
              </Link>
            </div>
          ))}
        </div>
      )}

      {!loading && lawyers.length === 0 && (
        <div className="no-results">
          <p>No lawyers found matching your criteria.</p>
        </div>
      )}
    </div>
  );
}

export default PublicSearch;
